-- ----------------------------
-- Table structure for `user`
-- ----------------------------
DROP TABLE IF EXISTS `user`;
CREATE TABLE `user` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_name` varchar(255) DEFAULT NULL,
  `nick_name` varchar(255) DEFAULT NULL,
  `login_name` varchar(255) NOT NULL DEFAULT 'test_user',
  `password` varchar(255) NOT NULL DEFAULT '222000',
  `gender` char(255) NOT NULL DEFAULT 'M',
  `portrait` varchar(255) NOT NULL DEFAULT 'default',
  `authority` int(11) NOT NULL DEFAULT '10',
  `email` varchar(255) DEFAULT NULL,
  `qq` varchar(255) DEFAULT NULL,
  `phone` varchar(255) DEFAULT NULL,
  `last_login_ip` varchar(255) NOT NULL DEFAULT '0.0.0.0',
  `last_login_date` varchar(255) NOT NULL DEFAULT '1970-1-1',
  `last_login_time` varchar(255) NOT NULL DEFAULT '00:00',
  `del_flag` tinyint(1) NOT NULL DEFAULT '0',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

