DROP PROCEDURE IF EXISTS `procShow`;
DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `procShow`()
BEGIN
 	declare n bigint;
	set n = 0;
	while n <= 200
	do
		insert into Essay (content) values('dddddddd');
		set n = n + 1;
	end while;

END
;;
DELIMITER ;

